INSERT INTO public.work (created_date, status, updated_date, dimension, name, note, price) VALUES (CURRENT_TIMESTAMP AT TIME ZONE 'Europe/Moscow', 'ACTIVE', null, 'порт', 'Монтажно-кабельные работы (за порт) ЛВС/СКС', 'Максимальная цена для кабеля длинной от 40м и больше (до 120 для ЛВС и более для телефонной сети), в зависимости от сложности (например прокладка кабеля в гофре), может увеличиться. Учитывается "обжим RJ-45, RJ-11" или расшивка на патч-панель с одной стороны, и розетка  "RJ-45, RJ-11" или расшивка на патч-панель с другой стороны, а так же прокладка кабеля от точки "А" до точки "Б".', 1500);
